-- Add siteid to the custom field group temorarily to make our life easier.
ALTER TABLE CustomFieldGroup ADD SiteId bigint;
GO

UPDATE CustomFieldGroup 
  SET SiteId = (
    SELECT top 1 wa.SiteId
    FROM dbo.WorkAssignment wa
    INNER JOIN dbo.CustomFieldGroupWorkAssignment cwa ON cwa.WorkAssignmentId = wa.Id
    INNER JOIN dbo.CustomFieldGroup cfg ON cwa.CustomFieldGroupId = cfg.Id
    where cfg.Id = CustomFieldGroup.Id)
GO

-- Delete the existing custom field group to work assignment associations for Missisauga
DELETE CustomFieldGroupWorkAssignment 
FROM CustomFieldGroupWorkAssignment
INNER JOIN WorkAssignment ON CustomFieldGroupWorkAssignment.WorkAssignmentId = WorkAssignment.Id
and WorkAssignment.SiteId = 10
GO

-- Delete the existing custom fields for Missisauga
DELETE 
CustomField FROM CustomField
	INNER JOIN dbo.CustomFieldGroup ON CustomField.CustomFieldGroupId = dbo.CustomFieldGroup.id
WHERE 
  dbo.CustomFieldGroup.SiteId = 10
GO

-- Delete the existing custom field groups for Missisauga
DELETE CustomFieldGroup
  WHERe SiteId = 10
GO  
  

